import { ref, onMounted } from "vue";
import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css";
import "../assets/css/custom.css";
import pt from "grapesjs/src/i18n/locale/pt";
import parserPostCSS from "grapesjs-parser-postcss";
import customCode from "grapesjs-custom-code";
import scriptEditor from "grapesjs-script-editor";
import cuboForm from "../plugins/cubosuite-form";
import borders from "grapesjs-plugin-borders";
import webpage from "grapesjs-preset-webpage";
import basicBlocs from "grapesjs-blocks-basic";
export default {
  __name: 'Pagebuilder',
  setup(__props, {
    expose
  }) {
    expose();
    onMounted(() => {
      const editor = grapesjs.init({
        container: "#gjs",
        height: "94vh",
        width: "100vw",
        i18n: {
          locale: pt,
          localeFallback: "pt",
          messages: {
            pt
          }
        },
        plugins: [parserPostCSS, customCode, scriptEditor, cuboForm, borders, webpage, basicBlocs],
        blockManager: {
          appendTo: document.querySelector('#blocks')
        },
        styleManager: {
          appendTo: document.querySelector('#style')
        },
        layerManager: {
          appendTo: document.querySelector('#layers')
        }
      });
      editor.Panels.addPanel({
        id: 'devices',
        el: ".devices",
        buttons: [{
          id: "set-device-desktop",
          command: function (e) {
            return e.setDevice("Desktop");
          },
          className: "fa fa-desktop",
          active: 1
        }, {
          id: "set-device-tablet",
          command: function (e) {
            return e.setDevice("Tablet");
          },
          className: "fa fa-tablet"
