        return grapesjs;
      },
      get pt() {
        return pt;
      },
      get parserPostCSS() {
        return parserPostCSS;
      },
      get customCode() {
        return customCode;
      },
      get scriptEditor() {
        return scriptEditor;
      },
      get cuboForm() {
        return cuboForm;
      },
      get borders() {
        return borders;
      },
      get webpage() {
        return webpage;
      },
      get basicBlocs() {
        return basicBlocs;
      }
